package April28;

public class CharAtMethod 
{

	public static void main(String[] args)
	{
		// TODO Auto-generated method stub
		String obj1="India Maharashtra";
		char ch=obj1.charAt(0);
		
		System.out.println(ch);
		System.out.println(obj1.charAt(obj1.length()-1));
		System.out.println(obj1.charAt(16));
	}

}
